import React from "react";

const Input = (name, label, value, onChange) => {
  return (
    <div className="form-group">
      <label htmlFor={name}>{label}</label>
      <input
        autoFocus
        ref={name}
        id={name}
        name={name}
        type="text"
        className="form-control"
        onChange={onChange}
        value={value}
      />
    </div>
  );
};

export default Input;
